mapper.select:


    device_type = 1
    and DEVICE_CODE = #{deviceCode}
    and DEVICE_DOMAIN = #{deviceDomain}
    and DEVICE_NAME like concat('%', #{deviceName}, '%')

    and MAC_ADDRESS = #{macAddress}
    and LONGITUDE = #{longitude}
    and LATITUDE = #{latitude}
    and REGION = #{region}
    and CREATE_TIME = #{createTime}
    and UPDATE_TIME = #{updateTime}
    and IP_ADDRESS = #{ipAddress}
    and STATUS = #{status}
    and SIP_SERVER_ID = #{sipServerId}
    and DOMAIN_IP = #{domainIp}
    and DOMAIN_PORT = #{domainPort}
    and IS_GB = #{isGb}
    and MANUFACTURER = #{manufacturer}
    and IS_PROBE = #{isProbe}
    and IS_IMPORT = #{isImport}
    and IS_CHECK = #{isCheck}
    and update_time between STR_TO_DATE(#{startDate},
        '%Y-%m-%dT%H:%i:%s') and STR_TO_DATE(#{endDate}, '%Y-%m-%dT%H:%i:%s')


order by update_time desc


where
device_type = 1
and DEVICE_CODE in
(SELECT DISTINCT
bs.dst_code
FROM
busi_statistics bs
WHERE
    1=1


        and bs.stamp BETWEEN DATE_FORMAT(#{startDate}, '%y%m%d') and DATE_FORMAT(#{endDate}, '%y%m%d')


        and bs.stamp BETWEEN DATE_FORMAT(now(), '%y%m%d') and DATE_FORMAT(now(), '%y%m%d')


)
and DEVICE_DOMAIN = #{deviceDomain}
and DEVICE_NAME like concat('%', #{deviceName}, '%')

