mapper.select:
SELECT DEVICE_CODE ,DEVICE_NAME ,IP_ADDRESS ,REGION_NAME ,CREATE_TIME
from busi_camera
WHERE 1 = 1
AND DEVICE_TYPE = 1

    AND create_time >= #{stime}


    AND create_time <= #{etime}


    AND IP_ADDRESS = #{ip}


    AND DEVICE_CODE = #{deviceCode}


    AND DEVICE_NAME like concat(#{deviceName}, '%')


    AND id in (select DISTINCT camera_id from busi_oper where session_id = #{sessionId}  AND (action <> 10 and action <> 11 ))

select.where:
device_code in

    #{item}

and DEVICE_CODE = #{filter.deviceCode}

and DEVICE_NAME like concat('%',
    #{filter.deviceName}, '%')

and IP_ADDRESS = #{filter.ipAddress}
and REGION = #{filter.region}
and device_type = 1

mapper.select:
select
*
from
BUSI_CAMERA
where
ID in (${id})
and DEVICE_TYPE = 1

    AND DEVICE_CODE = #{deviceCode}


    AND IP_ADDRESS = #{ipAddress}


    order by ${orderType}

mapper.update:
update busi_camera
DEVICE_CODE = #{deviceCode}, DEVICE_DOMAIN = #{deviceDomain}, DEVICE_NAME = #{deviceName}, MAC_ADDRESS = #{macAddress}, LONGITUDE = #{longitude}, LATITUDE = #{latitude}, REGION = #{region}, CREATE_TIME = #{createTime}, UPDATE_TIME = #{updateTime}, IP_ADDRESS = #{ipAddress}, STATUS = #{status}, SIP_SERVER_ID = #{sipServerId}, DEVICE_TYPE = #{deviceType}, DOMAIN_IP = #{domainIp}, DOMAIN_PORT = #{domainPort}, IS_GB = #{isGb}, MANUFACTURER = #{manufacturer}, IS_PROBE = #{isProbe}, IS_IMPORT = #{isImport}, IS_CHECK = #{isCheck},
where ID = #{id}
