mapper.select:
SELECT v.* FROM busi_oper v


        AND v.ID = #{id}


        AND v.SRC_IP = #{srcIp}


        AND v.DST_IP = #{dstIp}


        AND v.SRC_CODE = #{srcCode}


        AND v.DST_CODE = #{dstCode}


        AND v.SESSION_ID = #{sessionId, jdbcType=BIGINT}


        AND v.CLIENT_ID = #{clientId}


        AND v.CAMERA_ID = #{cameraId}


        AND v.ACTION = #{action}


    and v.stamp >= STR_TO_DATE(#{startDate}, '%Y-%m-%d %H:%i:%s') and v.stamp <= STR_TO_DATE(#{endDate}, '%Y-%m-%d %H:%i:%s')


        AND v.dst_device_ip = #{dstDeviceIp}


        AND v.CALL_ID = #{callId}


        AND v.DST_DEVICE_NAME = #{dstDeviceName}


        AND v.CONTENT like concat('%', #{content}, '%')


        AND v.USERNAME = #{username}


        AND substr(v.DST_CODE,0,2) = #{provinceRegion}


        AND substr(v.DST_CODE,0,4) = #{cityRegion}


        AND substr(v.DST_CODE,0,6) = #{countryRegion}


        AND v.CONNECT_TYPE = #{connectType}




    ORDER BY v.STAMP DESC



    ORDER BY ${orderType}


limit #{num},#{size}

select * from busi_oper bo


    DST_CODE in

        #{item}



        AND bo.stamp >= STR_TO_DATE(#{filter.startDate}, '%Y-%m-%d %H:%i:%s') and bo.stamp <= STR_TO_DATE(#{filter.endDate}, '%Y-%m-%d %H:%i:%s')


        AND bo.SRC_IP = #{filter.srcIp}


        AND bo.DST_CODE = #{filter.dstCode}


        AND bo.ACTION = #{filter.action}


        AND bo.DST_IP = #{filter.dstIp}


        AND bo.USERNAME = #{filter.username}


        AND bo.DST_DEVICE_NAME = #{filter.dstDeviceName}



    ORDER BY bo.STAMP DESC
    limit #{filter.num},#{filter.size}

select count(1) from busi_oper v


        AND v.ID = #{id}


        AND v.SRC_IP = #{srcIp}


        AND v.DST_IP = #{dstIp}


        AND v.SRC_CODE = #{srcCode}


        AND v.DST_CODE = #{dstCode}


        AND v.SESSION_ID = #{sessionId, jdbcType=BIGINT}


        AND v.CLIENT_ID = #{clientId}


        AND v.CAMERA_ID = #{cameraId}


        AND v.ACTION = #{action}

    and v.stamp between STR_TO_DATE(#{startDate}, '%Y-%m-%d %H:%i:%s') and STR_TO_DATE(#{endDate}, '%Y-%m-%d %H:%i:%s')

        AND v.dst_device_ip = #{dstDeviceIp}


        AND v.CALL_ID = #{callId}


        AND v.DST_DEVICE_NAME = #{dstDeviceName}


        AND v.CONTENT like concat('%', #{content}, '%')


        AND v.USERNAME = #{username}


        AND substr(v.DST_CODE,0,2) = #{provinceRegion}


        AND substr(v.DST_CODE,0,4) = #{cityRegion}


        AND substr(v.DST_CODE,0,6) = #{countryRegion}


        AND v.CONNECT_TYPE = #{connectType}

SELECT bc.ID as id, bc.DEVICE_CODE as dstCode, bc.IP_ADDRESS as ipAddress, bc.USERNAME as username, bc.CREATE_TIME as createTime, bc.UPDATE_TIME as updateTime, t.count as count FROM (SELECT DISTINCT CLIENT_ID , count(1) AS count FROM busi_oper bo WHERE 1=1 AND bo.ACTION != 6
AND bo.CAMERA_ID = #{cameraId}
and bo.stamp between STR_TO_DATE(#{startDate}, '%Y-%m-%dT%H:%i:%s') and STR_TO_DATE(#{endDate}, '%Y-%m-%dT%H:%i:%s')
and bo.ACTION = #{action}
GROUP BY CLIENT_ID ) t LEFT JOIN BUSI_CLIENT bc ON bc.ID = t.CLIENT_ID
limit #{num} , #{size}

SELECT count(distinct CLIENT_ID) AS count FROM busi_oper bo WHERE 1=1 AND bo.ACTION != 6
AND bo.CAMERA_ID = #{cameraId}
and bo.stamp between STR_TO_DATE(#{startDate}, '%Y-%m-%dT%H:%i:%s') and STR_TO_DATE(#{endDate}, '%Y-%m-%dT%H:%i:%s')
and bo.ACTION = #{action}
GROUP BY CLIENT_ID
